tables.table_schema:
CREATE TABLE insurance_policies (
    policy_id INT PRIMARY KEY,
    farmer_id INT,
    premium_amount DECIMAL,
    risk_factor DECIMAL
);

CREATE TABLE farmers (
    farmer_id INT PRIMARY KEY,
    farmer_name VARCHAR(50),
    farmer_address VARCHAR(100),
    farmer_age INT
);

CREATE TABLE risk_factors (
    factor_id INT PRIMARY KEY,
    factor_description VARCHAR(200),
    factor_weight DECIMAL
);

CREATE TABLE Farmer (
  farmer_id INT PRIMARY KEY,
  farmer_name VARCHAR(50) NOT NULL,
  farmer_address VARCHAR(100) NOT NULL,
  farmer_contact VARCHAR(20) NOT NULL
);

CREATE TABLE Crop (
  crop_id INT PRIMARY KEY,
  crop_name VARCHAR(50) NOT NULL,
  crop_type VARCHAR(50) NOT NULL,
  crop_yield DECIMAL(10,2) NOT NULL
);

CREATE TABLE InsurancePolicy (
  policy_id INT PRIMARY KEY,
  policy_type VARCHAR(50) NOT NULL,
  policy_coverage DECIMAL(10,2) NOT NULL,
  policy_premium DECIMAL(10,2) NOT NULL
);

CREATE TABLE RiskFactor (
  risk_id INT PRIMARY KEY,
  risk_description VARCHAR(100) NOT NULL,
  risk_value DECIMAL(10,2) NOT NULL,
  risk_impact VARCHAR(200) NOT NULL
);

CREATE TABLE farmers (
    farmer_id SERIAL PRIMARY KEY,
    farmer_name VARCHAR(50) NOT NULL,
    farmer_location VARCHAR(100) NOT NULL
);

CREATE TABLE crops (
    crop_id SERIAL PRIMARY KEY,
    crop_name VARCHAR(50) NOT NULL,
    crop_type VARCHAR(50) NOT NULL,
    crop_value DECIMAL(10, 2) NOT NULL
);

CREATE TABLE risk_factors (
    factor_id SERIAL PRIMARY KEY,
    factor_name VARCHAR(50) NOT NULL,
    factor_description TEXT NOT NULL
);

CREATE TABLE insurance_policies (
    policy_id SERIAL PRIMARY KEY,
    policy_name VARCHAR(100) NOT NULL,
    premium_calculation_id INT NOT NULL
);
