tables.table_schema:
CREATE TABLE field_moisture_data (
    field_id INT PRIMARY KEY,
    field_name TEXT,
    soil_moisture_level FLOAT,
    last_updated TIMESTAMP
);

CREATE TABLE field_data ( 
    field_id UUID PRIMARY KEY, 
    field_name STRING NOT NULL, 
    soil_moisture_level FLOAT NOT NULL, 
    last_updated TIMESTAMP NOT NULL 
);

CREATE TABLE irrigation_schedule ( 
    schedule_id UUID PRIMARY KEY, 
    field_id UUID REFERENCES field_data(field_id), 
    irrigation_date TIMESTAMP NOT NULL, 
    irrigation_duration INTERVAL NOT NULL 
);

CREATE TABLE fields (
    field_id SERIAL PRIMARY KEY,
    field_name VARCHAR(255) NOT NULL,
    soil_moisture_level DECIMAL NOT NULL
);

CREATE TABLE irrigation_schedules (
    schedule_id SERIAL PRIMARY KEY,
    field_id INT REFERENCES fields(field_id),
    start_time TIMESTAMP NOT NULL,
    end_time TIMESTAMP NOT NULL
);

CREATE TABLE moisture_readings (
    reading_id SERIAL PRIMARY KEY,
    field_id INT REFERENCES fields(field_id),
    reading_time TIMESTAMP NOT NULL,
    moisture_level DECIMAL NOT NULL
);

CREATE TABLE field(
  id INT PRIMARY KEY,
  name VARCHAR(255) NOT NULL,
  location VARCHAR(255) NOT NULL
);
