tables.table_schema:
CREATE TABLE crop_diseases (
    disease_id INT PRIMARY KEY,
    disease_name VARCHAR(50),
    crop_affected VARCHAR(50),
    date_discovered DATE
);

CREATE TABLE preventive_measures (
    measure_id INT PRIMARY KEY,
    measure_name VARCHAR(50),
    recommended_crop VARCHAR(50),
    effectivity_rating INT
);

CREATE TABLE data_sources (
    source_id UUID,
    source_name STRING,
    data_type STRING,
    description TEXT
);
