tables.table_schema:
CREATE TABLE water_usage_reports (
    report_id INT PRIMARY KEY,
    report_date DATE,
    farm_id INT,
    water_usage FLOAT
);

CREATE TABLE farm_details (
    farm_id INT PRIMARY KEY,
    farm_name VARCHAR(255),
    location VARCHAR(255),
    acreage FLOAT
);

CREATE TABLE water_sources (
    id INT PRIMARY KEY,
    source_name STRING,
    location STRING,
    capacity INT
);

CREATE TABLE farming_activities (
    id INT PRIMARY KEY,
    activity_name STRING,
    start_date DATE,
    end_date DATE
);

CREATE TABLE WaterSources (
  id INT PRIMARY KEY,
  source_name VARCHAR(50) NOT NULL,
  location VARCHAR(100) NOT NULL,
  water_type ENUM('groundwater', 'surface water', 'rainwater') NOT NULL
);

CREATE TABLE Farms (
  farm_id INT PRIMARY KEY,
  farm_name VARCHAR(100) NOT NULL,
  location VARCHAR(100) NOT NULL,
  size_acres DECIMAL(10,2) NOT NULL
);
