tables.table_schema:
CREATE TABLE fields (
  field_id UUID PRIMARY KEY,
  field_name VARCHAR(255),
  field_location GEOGRAPHY
);

CREATE TABLE crop_health (
  record_id UUID PRIMARY KEY,
  field_id UUID,
  health_status VARCHAR(50),
  health_timestamp TIMESTAMP
);

CREATE TABLE FieldMapping (
    field_id INT PRIMARY KEY,
    latitude DECIMAL,
    longitude DECIMAL,
    field_name TEXT
);

CREATE TABLE FieldMapping (
    field_id INT PRIMARY KEY,
    latitude DECIMAL,
    longitude DECIMAL,
    field_area DECIMAL
);
