tables.table_schema:
CREATE TABLE farm_activities ( 
    id INT PRIMARY KEY, 
    activity_type VARCHAR(50), 
    activity_date DATE, 
    activity_description TEXT 
);

CREATE TABLE farm_activities (
  activity_id SERIAL PRIMARY KEY,
  activity_name TEXT,
  activity_date DATE
);

CREATE TABLE drone_images (
  image_id SERIAL PRIMARY KEY,
  image_name TEXT,
  image_date DATE,
  drone_id INT REFERENCES drones(drone_id)
);

CREATE TABLE drones (
  drone_id SERIAL PRIMARY KEY,
  drone_name TEXT,
  drone_model TEXT
);

CREATE TABLE drones (
  drone_id INT PRIMARY KEY,
  drone_type VARCHAR(50),
  flight_range_km FLOAT,
  max_speed_kmh INT
);

CREATE TABLE farms (
  farm_id INT PRIMARY KEY,
  farm_name VARCHAR(100),
  farm_location VARCHAR(100),
  farm_size_acres FLOAT
);
