tables.table_schema:
CREATE TABLE genetically_modified_crops (
    crop_id INT PRIMARY KEY,
    crop_name VARCHAR(50) NOT NULL,
    genetic_type VARCHAR(50) NOT NULL,
    modified_trait VARCHAR(100) NOT NULL
);

CREATE TABLE crops (
  crop_id INT PRIMARY KEY,
  crop_name VARCHAR(50),
  scientific_name VARCHAR(100),
  growth_period INT
);

CREATE TABLE characteristics (
  char_id INT PRIMARY KEY,
  crop_id INT REFERENCES crops(crop_id),
  trait VARCHAR(50),
  value VARCHAR(50)
);
