tables.table_schema:
CREATE TABLE region_usage (
	region_id INT PRIMARY KEY,
	region_name VARCHAR(255) NOT NULL,
	gmo_seed_usage INT
);

CREATE TABLE seed_supplier (
	supplier_id INT PRIMARY KEY,
	supplier_name VARCHAR(255) NOT NULL,
	contact_person VARCHAR(255),
	email_address VARCHAR(255)
);

CREATE TABLE regions (
    region_id INT PRIMARY KEY,
    region_name VARCHAR(50) NOT NULL
);

CREATE TABLE crops (
    crop_id INT PRIMARY KEY,
    crop_name VARCHAR(50) NOT NULL,
    crop_description TEXT
);

CREATE TABLE seeds (
    seed_id INT PRIMARY KEY,
    seed_name VARCHAR(50) NOT NULL,
    crop_id INT REFERENCES crops (crop_id),
    region_id INT REFERENCES regions (region_id)
);

CREATE TABLE seed_usage (
    usage_id INT PRIMARY KEY,
    seed_id INT REFERENCES seeds (seed_id),
    usage_date DATE,
    usage_quantity INT
);
