tables.table_schema:
CREATE TABLE crops (
  crop_id INT PRIMARY KEY,
  crop_name VARCHAR(50),
  season_year INT,
  yield_kg INT
);

CREATE TABLE gmos (
  gmo_id INT PRIMARY KEY,
  gmo_name VARCHAR(50),
  trait VARCHAR(50),
  experimental_group VARCHAR(50)
);

CREATE TABLE crop_data (
    crop_id INT PRIMARY KEY,
    crop_name VARCHAR(255),
    yield_per_season FLOAT
);

CREATE TABLE growing_season (
    season_id INT PRIMARY KEY,
    season_year INT,
    start_date DATE,
    end_date DATE
);

CREATE TABLE crops (
    crop_id SERIAL PRIMARY KEY,
    crop_name VARCHAR(255) NOT NULL,
    yield_in_tons DECIMAL,
    season INT
);

CREATE TABLE gmos (
    gmo_id SERIAL PRIMARY KEY,
    gmo_name VARCHAR(255) NOT NULL,
    gmo_type VARCHAR(50),
    effectiveness DECIMAL
);

CREATE TABLE growing_seasons (
    season_id SERIAL PRIMARY KEY,
    year INT NOT NULL,
    start_date DATE,
    end_date DATE
);

CREATE TABLE test_fields (
    field_id SERIAL PRIMARY KEY,
    location VARCHAR(255) NOT NULL,
    acres DECIMAL,
    soil_type VARCHAR(50)
);
