tables.table_schema:
CREATE TABLE gmo_products (
    id INT PRIMARY KEY,
    product_name VARCHAR(255),
    distributor_name VARCHAR(255),
    distribution_date DATE
);

CREATE TABLE compliance_regulations (
    id INT PRIMARY KEY,
    regulation_name VARCHAR(255),
    regulation_description TEXT,
    jurisdiction VARCHAR(255)
);

CREATE TABLE distribution_centers (
    center_id INT PRIMARY KEY,
    center_name VARCHAR(255),
    location VARCHAR(255),
    manager_id INT
);

CREATE TABLE products (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(255),
    manufacturer VARCHAR(255),
    is_gmo BOOLEAN
);

CREATE TABLE regulatory_agencies (
    agency_id INT PRIMARY KEY,
    agency_name VARCHAR(255),
    contact_person VARCHAR(255),
    region VARCHAR(255)
);

CREATE TABLE Product ( 
   id INT PRIMARY KEY, 
   name VARCHAR(255) NOT NULL, 
   category VARCHAR(50) NOT NULL, 
   is_gmo BOOLEAN NOT NULL
);

CREATE TABLE Distributor ( 
   id INT PRIMARY KEY, 
   name VARCHAR(255) NOT NULL, 
   location VARCHAR(100) NOT NULL
);

CREATE TABLE Compliance ( 
   id INT PRIMARY KEY, 
   product_id INT REFERENCES Product(id), 
   distributor_id INT REFERENCES Distributor(id), 
   compliance_status VARCHAR(50) NOT NULL
);

CREATE TABLE Inspection ( 
   id INT PRIMARY KEY, 
   compliance_id INT REFERENCES Compliance(id), 
   inspector VARCHAR(255) NOT NULL, 
   inspection_date DATE NOT NULL
);

CREATE TABLE Violation ( 
   id INT PRIMARY KEY, 
   inspection_id INT REFERENCES Inspection(id), 
   violation_type VARCHAR(50) NOT NULL, 
   description TEXT
);
