tables.table_schema:
CREATE TABLE genetic_modifications (
    id INT PRIMARY KEY,
    crop_name VARCHAR(255) NOT NULL,
    modification_type VARCHAR(255) NOT NULL,
    pest_resistance BOOLEAN
);

CREATE TABLE crops (
    crop_id INT PRIMARY KEY,
    crop_name VARCHAR(255),
    pest_resistance BOOLEAN
);

CREATE TABLE CropGeneticModifications (
  CropID UUID PRIMARY KEY,
  ModificationType VARCHAR(255),
  ModificationDate DATE,
  PestResistance BOOL
);

CREATE TABLE PestInfo (
  PestID UUID PRIMARY KEY,
  PestName VARCHAR(255),
  PestType VARCHAR(255),
  PestDescription TEXT
);
