tables.table_schema:
CREATE TABLE crops (
    crop_id INT PRIMARY KEY,
    crop_name VARCHAR(255) NOT NULL,
    crop_species VARCHAR(255) NOT NULL,
    genetic_traits TEXT
);

CREATE TABLE genetic_modifications (
    modification_id INT PRIMARY KEY,
    modification_type VARCHAR(255) NOT NULL,
    modification_target VARCHAR(255) NOT NULL,
    modification_description TEXT
);

CREATE TABLE genetic_modifications (
    id INT PRIMARY KEY,
    modification_type VARCHAR(50) NOT NULL,
    modification_description TEXT
);

CREATE TABLE crop_species (
  species_id INT PRIMARY KEY,
  species_name VARCHAR(50) NOT NULL
);

CREATE TABLE genetic_markers (
  marker_id INT PRIMARY KEY,
  marker_name VARCHAR(50) NOT NULL,
  chromosome INT,
  position INT
);

CREATE TABLE research_labs (
  lab_id INT PRIMARY KEY,
  lab_name VARCHAR(100) NOT NULL
);
