tables.table_schema:
CREATE TABLE research_findings_env_effects (
   id INT PRIMARY KEY,
   ecosystem_name VARCHAR(255),
   research_topic TEXT,
   findings TEXT
 );

CREATE TABLE research_findings (
    id INT PRIMARY KEY,
    ecosystem_name VARCHAR(255),
    gmo_type VARCHAR(255),
    effect_description TEXT
);

CREATE TABLE researchers (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(255),
    research_date DATE,
    research_location VARCHAR(255)
);
