tables.table_schema:
CREATE TABLE licenses (
    id INT PRIMARY KEY,
    license_number INT NOT NULL,
    license_description TEXT
);

CREATE TABLE patents (
    id INT PRIMARY KEY,
    patent_number INT UNIQUE NOT NULL,
    patent_description TEXT,
    patent_owner TEXT
);

CREATE TABLE technology (
    id UUID PRIMARY KEY,
    technology_name VARCHAR(100),
    description TEXT
);

CREATE TABLE licensing (
    id INT PRIMARY KEY,
    patent_number VARCHAR(50) NOT NULL,
    licensee VARCHAR(100) NOT NULL,
    issue_date DATE NOT NULL
);

CREATE TABLE patent (
    patent_id INT PRIMARY KEY,
    patent_number VARCHAR(50) NOT NULL,
    technology VARCHAR(100) NOT NULL,
    expiration_date DATE NOT NULL
);

CREATE TABLE licensee (
    licensee_id INT PRIMARY KEY,
    name VARCHAR(100) NOT NULL,
    address VARCHAR(200) NOT NULL,
    contact_person VARCHAR(100) NOT NULL
);

CREATE TABLE gmo_crop (
    crop_id INT PRIMARY KEY,
    crop_name VARCHAR(100) NOT NULL,
    patent_id INT NOT NULL,
    licensee_id INT NOT NULL
);
