tables.table_schema:
CREATE TABLE MarketTrends (
    id SERIAL PRIMARY KEY,
    product_name VARCHAR(50) NOT NULL,
    market_segment VARCHAR(50) NOT NULL,
    consumer_preference VARCHAR(50) NOT NULL
);

CREATE TABLE ConsumerFeedback (
    id SERIAL PRIMARY KEY,
    product_name VARCHAR(50) NOT NULL,
    feedback_text TEXT,
    rating INT
);

CREATE TABLE Market_Trends (
 	 id INT PRIMARY KEY,
 	 product_name VARCHAR(255),
 	 market_demand INT,
 	 consumer_feedback VARCHAR(1000)
);

CREATE TABLE Consumer_Preferences (
 	 id INT PRIMARY KEY,
 	 consumer_id INT,
 	 preferred_product_name VARCHAR(255),
 	 purchase_history JSON
);

CREATE TABLE Product_Inventory (
 	 id INT PRIMARY KEY,
 	 product_name VARCHAR(255),
 	 quantity_available INT,
 	 expiration_date DATE
);

CREATE TABLE Supplier_Information (
 	 id INT PRIMARY KEY,
 	 supplier_name VARCHAR(255),
 	 contact_information VARCHAR(255),
 	 product_categories JSON
);
