tables.table_schema:
CREATE TABLE Economic_Benefits (
   ID INT PRIMARY KEY,
   Crop_Name VARCHAR(50),
   GMO_Use BOOL,
   Economic_Value DECIMAL
);

CREATE TABLE Statistical_Analysis (
   Analysis_ID INT PRIMARY KEY,
   Experiment_Date DATE,
   Sample_Size INT,
   Result DECIMAL
);

CREATE TABLE Cost_Analysis (
   id INT PRIMARY KEY,
   crop_name VARCHAR(255),
   gmo_cost DECIMAL(10, 2),
   non_gmo_cost DECIMAL(10, 2)
);

CREATE TABLE economic_benefits_of_gmos (
 id SERIAL PRIMARY KEY,
 crop_name VARCHAR(50),
 gmo_effectiveness FLOAT,
 economic_benefits DECIMAL
);

CREATE TABLE crop_production_data (
 id SERIAL PRIMARY KEY,
 crop_name VARCHAR(50),
 production_year INT,
 yield_per_acre DECIMAL
);

CREATE TABLE economic_analysis (
 id SERIAL PRIMARY KEY,
 analysis_date DATE,
 benefits_score FLOAT,
 analysis_result TEXT
);
