tables.table_schema:
CREATE TABLE member_information (
    policy_number INT PRIMARY KEY,
    coverage_details TEXT,
    contact_information TEXT
);

CREATE TABLE MemberInformation (
  policy_number INT PRIMARY KEY,
  coverage_details TEXT,
  contact_information VARCHAR NOT NULL
);

CREATE TABLE ClaimsInformation (
  claim_id INT PRIMARY KEY,
  policy_number INT REFERENCES MemberInformation(policy_number),
  date_of_claim DATE,
  claim_amount DECIMAL
);

CREATE TABLE members (
    member_id INT PRIMARY KEY,
    policy_number VARCHAR(50) NOT NULL,
    coverage_details VARCHAR(255),
    contact_information VARCHAR(100)
);

CREATE TABLE contacts (
    contact_id INT PRIMARY KEY,
    member_id INT,
    contact_name VARCHAR(100),
    contact_email VARCHAR(100)
);

CREATE TABLE Policies (
    policy_number INT PRIMARY KEY,
    coverage_details TEXT,
    member_id INT REFERENCES Members(member_id)
);

CREATE TABLE Claims (
    claim_id INT PRIMARY KEY,
    policy_number INT REFERENCES Policies(policy_number),
    claim_description TEXT,
    claim_amount DECIMAL(10, 2)
);

CREATE TABLE Payments (
    payment_id INT PRIMARY KEY,
    claim_id INT REFERENCES Claims(claim_id),
    payment_amount DECIMAL(10, 2),
    payment_date DATE
);
