tables.table_schema:
CREATE TABLE Approvals (
  approval_id INT PRIMARY KEY,
  claim_id INT REFERENCES Claims(claim_id),
  approved_by VARCHAR(255),
  approval_date DATE
);

CREATE TABLE ReimbursementRequests (
   request_id INT PRIMARY KEY,
   request_date DATE,
   claimant_name VARCHAR(50),
   total_amount DECIMAL(10, 2)
);

CREATE TABLE Claims (
    claim_id INT PRIMARY KEY,
    claim_type VARCHAR(255),
    claim_amount DECIMAL(10, 2),
    claim_date DATE
);

CREATE TABLE Approvals (
    approval_id INT PRIMARY KEY,
    reimbursement_id INT,
    approver_name VARCHAR(255),
    approval_date DATE
);

CREATE TABLE Denials (
    denial_id INT PRIMARY KEY,
    claim_id INT,
    denial_reason VARCHAR(255),
    denial_date DATE
);

CREATE TABLE approval_records (
    id INT PRIMARY KEY,
    request_id INT,
    approver_id INT,
    approval_date DATE
);

CREATE TABLE denial_records (
    id INT PRIMARY KEY,
    request_id INT,
    reason TEXT,
    denial_date DATE
);

CREATE TABLE claimants (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    email VARCHAR(255),
    department VARCHAR(255)
);

CREATE TABLE approvers (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    role VARCHAR(255)
);
