tables.table_schema:
CREATE TABLE policy_coverage_details (
   policy_id INT PRIMARY KEY,
   coverage_type VARCHAR(255),
   premium_amount DECIMAL(10, 2),
   coverage_limit DECIMAL(12, 2)
);

CREATE TABLE PolicyCoverageDetails (
    policy_id INT PRIMARY KEY,
    coverage_type VARCHAR(255),
    premium_amount DECIMAL(10, 2),
    coverage_limit DECIMAL(12, 2)
);

CREATE TABLE coverage_type (
  coverage_id INT PRIMARY KEY,
  coverage_name VARCHAR(255) NOT NULL,
  description TEXT
);

CREATE TABLE premium_details (
  premium_id INT PRIMARY KEY,
  coverage_id INT NOT NULL,
  premium_amount DECIMAL(10,2) NOT NULL,
  FOREIGN KEY (coverage_id) REFERENCES coverage_type(coverage_id)
);

CREATE TABLE coverage_limits (
  limit_id INT PRIMARY KEY,
  coverage_id INT NOT NULL,
  max_limit DECIMAL(10,2) NOT NULL,
  FOREIGN KEY (coverage_id) REFERENCES coverage_type(coverage_id)
);

CREATE TABLE coverage_type (
  coverage_type_id INT PRIMARY KEY,
  name VARCHAR(50) NOT NULL
);

CREATE TABLE premium_amount (
  premium_id INT PRIMARY KEY,
  coverage_type_id INT,
  amount DECIMAL(10, 2) NOT NULL,
  FOREIGN KEY (coverage_type_id) REFERENCES coverage_type(coverage_type_id)
);

CREATE TABLE PolicyCoverageDetails (
    policy_id INT PRIMARY KEY,
    coverage_type VARCHAR(50),
    premium_amount DECIMAL(10, 2),
    coverage_limits TEXT
);

CREATE TABLE PolicyHolders (
    holder_id INT PRIMARY KEY,
    policy_id INT,
    holder_name VARCHAR(100),
    holder_address TEXT
);

CREATE TABLE PremiumPayments (
    payment_id INT PRIMARY KEY,
    policy_id INT,
    payment_date DATE,
    amount_paid DECIMAL(10, 2)
);

CREATE TABLE CoverageLimits (
    limit_id INT PRIMARY KEY,
    coverage_type VARCHAR(50),
    max_limit DECIMAL(12, 2),
    min_limit DECIMAL(12, 2)
);

CREATE TABLE PolicyRenewals (
    renewal_id INT PRIMARY KEY,
    policy_id INT,
    renewal_date DATE,
    new_premium_amount DECIMAL(10, 2)
);
