tables.table_schema:
CREATE TABLE home_assessments (
    assessment_id INT PRIMARY KEY,
    address VARCHAR NOT NULL,
    assessment_value DECIMAL NOT NULL
);

CREATE TABLE home_assessments (
    id INT PRIMARY KEY,
    address VARCHAR(255),
    area_sqft INT,
    assessment_value DECIMAL(10, 2)
);

CREATE TABLE home_assessments (
  assessment_id UUID PRIMARY KEY,
  assessment_date DATE,
  property_address TEXT,
  appraised_value DECIMAL
);

CREATE TABLE policy_underwriting (
  policy_id UUID PRIMARY KEY,
  policy_start_date DATE,
  policy_end_date DATE,
  policy_type TEXT
);

CREATE TABLE assessment_details (
  assessment_id UUID,
  assessor_name TEXT,
  assessment_notes TEXT
);

CREATE TABLE appraisals (
  appraisal_id UUID PRIMARY KEY,
  appraisal_date DATE,
  property_condition TEXT,
  appraisal_value DECIMAL
);

CREATE TABLE underwriting_details (
  policy_id UUID,
  underwriter_name TEXT,
  underwriting_notes TEXT
);
