tables.table_schema:
CREATE TABLE home_owners ( 
    id INT PRIMARY KEY, 
    name VARCHAR(50), 
    address VARCHAR(100), 
    policy_number VARCHAR(20) 
);

CREATE TABLE risk_factors ( 
    factor_id INT PRIMARY KEY, 
    claim_id INT, 
    factor_description TEXT 
);

CREATE TABLE claim_details (
	claim_id UUID PRIMARY KEY,
		issue_description TEXT,
		claim_status VARCHAR,
		claim_resolution_date DATE
);

CREATE TABLE damage_analysis (
	damage_id UUID PRIMARY KEY,
	claim_id UUID,
	damage_description TEXT,
	damage_cost DECIMAL
);

CREATE TABLE implicated_parties (
	claim_id UUID,
	policyholder_id UUID,
	witness_id UUID,
	agency_id UUID
);
