tables.table_schema:
CREATE TABLE Claims (
  claim_id SERIAL PRIMARY KEY,
  claim_description TEXT,
  claim_amount DECIMAL,
  claim_date DATE
);

CREATE TABLE FraudulentClaims (
  fraudulent_claim_id SERIAL PRIMARY KEY,
  claim_id INT REFERENCES Claims(claim_id),
  detection_method TEXT,
  detection_date DATE
);

CREATE TABLE claims_data (
  claim_id INT PRIMARY KEY,
  claim_date DATE,
  claim_amount DECIMAL,
  policy_holder_id INT
);

CREATE TABLE policy_holders (
  policy_holder_id INT PRIMARY KEY,
  policy_holder_name VARCHAR(255),
  policy_holder_dob DATE,
  policy_holder_address VARCHAR(255)
);

CREATE TABLE fraudulent_claims (
  fraudulent_claim_id INT PRIMARY KEY,
  claim_id INT,
  fraud_detection_score DECIMAL,
  fraud_detection_status VARCHAR(50)
);

CREATE TABLE Claims (
    claim_id INT PRIMARY KEY,
    claim_date DATE,
    customer_name VARCHAR,
    claim_amount DECIMAL
);

CREATE TABLE Customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR,
    customer_email VARCHAR,
    customer_phone VARCHAR
);

CREATE TABLE Transactions (
    transaction_id INT PRIMARY KEY,
    transaction_date DATE,
    amount DECIMAL,
    customer_id INT
);

CREATE TABLE Policies (
    policy_id INT PRIMARY KEY,
    policy_type VARCHAR,
    coverage DECIMAL,
    premium DECIMAL
);

CREATE TABLE FraudulentClaims (
    fraud_id INT PRIMARY KEY,
    claim_id INT,
    reason VARCHAR,
    reported_date DATE
);
