tables.table_schema:
CREATE TABLE underwriting_guidelines (
    id INT PRIMARY KEY,
    policy_type VARCHAR(50),
    age_limit INT,
    income_limit DECIMAL(10, 2)
);

CREATE TABLE policy_pricing (
    id INT PRIMARY KEY,
    policy_type VARCHAR(50),
    base_price DECIMAL(10, 2),
    risk_factor DECIMAL(5, 2)
);
