tables.table_schema:
CREATE TABLE policy_coverage_details (
    policy_id INT PRIMARY KEY,
    policy_holder_name VARCHAR(50),
    coverage_type VARCHAR(50),
    coverage_amount DECIMAL(10,2)
);

CREATE TABLE policy_updates (
    update_id INT PRIMARY KEY,
    policy_id INT,
    update_date DATE,
    update_description TEXT
);

CREATE TABLE PolicyCoverageDetails (
  policy_id INT PRIMARY KEY,
  policy_holder_name VARCHAR(255),
  coverage_type VARCHAR(50),
  coverage_amount DECIMAL
);

CREATE TABLE PolicyHolders (
    policy_holder_id INT PRIMARY KEY,
    first_name VARCHAR(50),
    last_name VARCHAR(50),
    address VARCHAR(100)
);

CREATE TABLE PolicyAgents (
    agent_id INT PRIMARY KEY,
    agent_name VARCHAR(50),
    contact_number VARCHAR(15),
    email VARCHAR(50)
);

CREATE TABLE PolicyClaims (
    claim_id INT PRIMARY KEY,
    policy_id INT,
    claim_date DATE,
    claim_amount DECIMAL(10,2)
);

CREATE TABLE PolicyPayments (
    payment_id INT PRIMARY KEY,
    policy_id INT,
    payment_date DATE,
    amount_paid DECIMAL(10,2)
);
