tables.table_schema:
CREATE TABLE PolicyInformation (
  policy_id UUID PRIMARY KEY,
  policy_holder_name VARCHAR(255),
  policy_start_date DATE,
  policy_end_date DATE
);

CREATE TABLE ClaimsAdjuster (
  adjuster_id UUID PRIMARY KEY,
  adjuster_name VARCHAR(255),
  adjuster_email VARCHAR(255),
  adjuster_phone VARCHAR(15)
);

CREATE TABLE Policy (
    policy_id INT PRIMARY KEY,
    policy_holder_name VARCHAR(255),
    policy_start_date DATE,
    policy_end_date DATE
);

CREATE TABLE Adjuster (
    adjuster_id INT PRIMARY KEY,
    adjuster_name VARCHAR(255),
    adjuster_email VARCHAR(255)
);

CREATE TABLE ClaimAdjuster (
    claim_id INT,
    adjuster_id INT,
    PRIMARY KEY (claim_id, adjuster_id)
);

CREATE TABLE ClaimHistory (
    history_id INT PRIMARY KEY,
    claim_id INT,
    claim_status VARCHAR(50),
    claim_notes TEXT
);
