tables.table_schema:
CREATE TABLE travel_insurance_policy (
    policy_id INT PRIMARY KEY,
    coverage_limit DECIMAL,
    premium_payment DECIMAL,
    renewal_date DATE
);

CREATE TABLE PolicyDetails (
  policy_id INT PRIMARY KEY,
  coverage_limits JSONB,
  premium_amount DECIMAL,
  renewal_date DATE
);

CREATE TABLE Customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR,
    address VARCHAR,
    phone_number VARCHAR
);

CREATE TABLE InsuranceCompanies (
    company_id INT PRIMARY KEY,
    company_name VARCHAR,
    location VARCHAR,
    contact_email VARCHAR
);

CREATE TABLE PolicyHistory (
    history_id INT PRIMARY KEY,
    policy_id INT,
    renewal_date DATE,
    premium_paid DECIMAL
);

CREATE TABLE coverage_limits (
   coverage_id INT PRIMARY KEY,
   coverage_name TEXT,
   max_coverage_amount DECIMAL
);

CREATE TABLE premium_payments (
   payment_id INT PRIMARY KEY,
   policy_id INT,
   payment_amount DECIMAL,
   payment_date DATE
);

CREATE TABLE renewal_dates (
   renewal_id INT PRIMARY KEY,
   policy_id INT,
   renewal_date DATE,
   renewal_amount DECIMAL
);

CREATE TABLE users (
   user_id INT PRIMARY KEY,
   user_name TEXT,
   user_email TEXT
);
