tables.table_schema:
CREATE TABLE risk_analysis (
  policy_id INT PRIMARY KEY,
  risk_factor VARCHAR,
  market_trend VARCHAR,
  claim_pattern VARCHAR
);

CREATE TABLE risk_factors (
  id INT PRIMARY KEY,
  name VARCHAR(255),
  type VARCHAR(50),
  value NUMERIC
);

CREATE TABLE claim_patterns (
  id INT PRIMARY KEY,
  claim_date DATE,
  claim_amount NUMERIC,
  customer_id INT
);

CREATE TABLE insurance_claim (
    claim_id INT PRIMARY KEY,
    customer_id INT,
    claim_amount DECIMAL,
    claim_date TIMESTAMP
);

CREATE TABLE customer (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR,
    customer_email VARCHAR,
    customer_address VARCHAR
);

CREATE TABLE policy (
    policy_id INT PRIMARY KEY,
    policy_type VARCHAR,
    policy_start_date TIMESTAMP,
    policy_end_date TIMESTAMP
);

CREATE TABLE risk_factors (
    factor_id INT PRIMARY KEY,
    factor_name VARCHAR,
    factor_description VARCHAR
);

CREATE TABLE market_trends (
    trend_id INT PRIMARY KEY,
    trend_name VARCHAR,
    trend_description VARCHAR
);
