tables.table_schema:
CREATE TABLE Medical_Records (
	record_id INT PRIMARY KEY,
	patient_name TEXT,
	diagnosis TEXT,
	date_of_visit DATE
);

CREATE TABLE Prescription_History (
	prescription_id INT PRIMARY KEY,
	patient_id INT,
	drug_name TEXT,
	prescription_date DATE
);

CREATE TABLE Medical_Records (
    record_id INT PRIMARY KEY,
    patient_name VARCHAR(50),
    date_of_visit DATE,
    diagnosis TEXT
);

CREATE TABLE medical_records (
    record_id INT PRIMARY KEY,
    patient_id INT,
    record_date DATE,
    record_details TEXT
);

CREATE TABLE patients (
    patient_id INT PRIMARY KEY,
    patient_name TEXT,
    date_of_birth DATE,
    gender TEXT
);

CREATE TABLE doctors (
    doctor_id INT PRIMARY KEY,
    doctor_name TEXT,
    specialization TEXT
);
