tables.table_schema:
CREATE TABLE PetOwners (
    owner_id INT PRIMARY KEY,
    owner_name VARCHAR(50) NOT NULL,
    contact_number VARCHAR(15) NOT NULL
);

CREATE TABLE Pets (
    pet_id INT PRIMARY KEY,
    pet_name VARCHAR(50) NOT NULL,
    pet_species VARCHAR(50) NOT NULL,
    owner_id INT REFERENCES PetOwners(owner_id)
);

CREATE TABLE Vaccinations (
    vaccination_id INT PRIMARY KEY,
    vaccination_name VARCHAR(50) NOT NULL,
    vaccination_date DATE NOT NULL,
    pet_id INT REFERENCES Pets(pet_id)
);

CREATE TABLE Appointments (
    appointment_id UUID PRIMARY KEY,
    pet_name VARCHAR(50),
    pet_type VARCHAR(50),
    appointment_date TIMESTAMP,
    veterinarian_name VARCHAR(100),
    vaccination_required BOOL
);

CREATE TABLE Veterinarians (
    veterinarian_id SERIAL PRIMARY KEY,
    veterinarian_name VARCHAR(100),
    specialty VARCHAR(100)
);

CREATE TABLE Pets (
    pet_id UUID PRIMARY KEY,
    pet_name VARCHAR(50),
    pet_type VARCHAR(50),
    owner_name VARCHAR(100),
    owner_contact VARCHAR(20)
);

CREATE TABLE Vaccinations (
    vaccination_id SERIAL PRIMARY KEY,
    pet_id UUID,
    vaccination_date TIMESTAMP,
    vaccination_type VARCHAR(50)
);

CREATE TABLE Owners (
    owner_id SERIAL PRIMARY KEY,
    owner_name VARCHAR(100),
    owner_contact VARCHAR(20),
    owner_address VARCHAR(200)
);
