tables.table_schema:
CREATE TABLE claims_data (
    claim_id SERIAL PRIMARY KEY,
    policy_number VARCHAR(50) NOT NULL,
    claim_date DATE NOT NULL,
    claim_amount DECIMAL(10, 2) NOT NULL
);

CREATE TABLE claimants (
    claimant_id SERIAL PRIMARY KEY,
    claim_id INT REFERENCES claims_data(claim_id),
    claimant_name VARCHAR(100) NOT NULL,
    claimant_address VARCHAR(255) NOT NULL
);

CREATE TABLE investigations (
    investigation_id SERIAL PRIMARY KEY,
    claim_id INT REFERENCES claims_data(claim_id),
    investigator_name VARCHAR(100) NOT NULL,
    investigation_date DATE NOT NULL
);

CREATE TABLE fraud_patterns (
    pattern_id SERIAL PRIMARY KEY,
    investigation_id INT REFERENCES investigations(investigation_id),
    fraudulent_activity_description TEXT NOT NULL
);
