tables.table_schema:
CREATE TABLE compliance_policies (
	policy_id SERIAL PRIMARY KEY,
	policy_name VARCHAR(50) NOT NULL,
	regulatory_requirement VARCHAR(100) NOT NULL
);

CREATE TABLE Policy_Compliance (
    policy_id INT PRIMARY KEY,
    policy_name VARCHAR(50),
    policy_description TEXT
);

CREATE TABLE Regulatory_Requirements (
    requirement_id INT PRIMARY KEY,
    requirement_name VARCHAR(50),
    requirement_description TEXT
);

CREATE TABLE Audit_Logs (
    log_id INT PRIMARY KEY,
    user_id INT,
    action_performed VARCHAR(50),
    timestamp TIMESTAMP
);

CREATE TABLE compliance_records(
    id INT PRIMARY KEY,
    policy_name VARCHAR(50),
    description TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE audit_logs(
    log_id INT PRIMARY KEY,
    action_performed VARCHAR(50),
    user_id INT,
    timestamp TIMESTAMP
);

CREATE TABLE regulatory_requirements(
    requirement_id INT PRIMARY KEY,
    requirement_name VARCHAR(50),
    description TEXT,
    category VARCHAR(50)
);

CREATE TABLE policy_mappings(
    mapping_id INT PRIMARY KEY,
    policy_id INT,
    requirement_id INT,
    mapping_description TEXT
);

CREATE TABLE monitoring_policy_compliance (
	record_id SERIAL PRIMARY KEY,
	record_date DATE,
	policy_name VARCHAR(255),
	policy_description TEXT
);

CREATE TABLE regulatory_requirements (
	requirement_id SERIAL PRIMARY KEY,
	requirement_name VARCHAR(255),
	requirement_description TEXT,
	requirement_category VARCHAR(50)
);

CREATE TABLE audit_records (
	audit_id SERIAL PRIMARY KEY,
	audit_date DATE,
	audit_description TEXT,
	audit_result VARCHAR(10)
);

CREATE TABLE employee_records (
	employee_id SERIAL PRIMARY KEY,
	employee_name VARCHAR(255),
	employee_department VARCHAR(100),
	employee_position VARCHAR(100)
);

CREATE TABLE compliance_actions (
	action_id SERIAL PRIMARY KEY,
	action_date DATE,
	action_description TEXT,
	audit_id INT REFERENCES audit_records(audit_id)
);
