    phone_number VARCHAR(15)
);

CREATE TABLE PropertyOwners (
  owner_id INT PRIMARY KEY,
  owner_name VARCHAR(50) NOT NULL,
  property_address VARCHAR(100) NOT NULL
);

CREATE TABLE property_owners (
    owner_id INT PRIMARY KEY,
