CREATE TABLE claim_items (
    item_id INT PRIMARY KEY,
    claim_id INT,
    item_description TEXT,
    item_value DECIMAL(10, 2)
);

CREATE TABLE adjusters (
    adjuster_id INT PRIMARY KEY,
    adjuster_name VARCHAR(50),
    adjuster_email VARCHAR(50),
    adjuster_phone VARCHAR(15)
);

CREATE TABLE claim_assignments (
    assign_id INT PRIMARY KEY,
    claim_id INT,
    adjuster_id INT,
    assignment_date DATE
);
