tables.table_schema:
CREATE TABLE RiskAssessment (
    policy_id INT PRIMARY KEY,
    customer_id INT,
    risk_score FLOAT,
    risk_factors STRING
 );

CREATE TABLE InsurancePremiums (
    policy_id INT PRIMARY KEY,
    customer_id INT,
    premium_amount FLOAT,
    coverage_type STRING
 );

CREATE TABLE RiskAssessment (
    id SERIAL PRIMARY KEY,
    customer_name VARCHAR(50),
    risk_level INT,
    assessment_date TIMESTAMP
);

CREATE TABLE historical_data ( 
   id INT PRIMARY KEY, 
   date DATE, 
   description STRING 
);

CREATE TABLE risk_levels ( 
   id INT PRIMARY KEY, 
   level STRING, 
   weight INT 
);

CREATE TABLE insurance_premiums ( 
   id INT PRIMARY KEY, 
   premium_rate FLOAT, 
   coverage_amount INT 
);
