tables.table_schema:
CREATE TABLE policy_expiration (
    policy_id INT PRIMARY KEY,
    property_owner_id INT,
    expiration_date DATE
);
CREATE TABLE reminder_notifications (
    notification_id INT PRIMARY KEY,
    policy_id INT,
    notification_date DATE
);

CREATE TABLE PolicyOwners (
   owner_id INT PRIMARY KEY,
   owner_name VARCHAR(50) NOT NULL,
   email VARCHAR(100) UNIQUE NOT NULL
);

CREATE TABLE Policies (
   policy_id INT PRIMARY KEY,
   owner_id INT REFERENCES PolicyOwners(owner_id),
   policy_start_date DATE NOT NULL,
   policy_end_date DATE NOT NULL
);

CREATE TABLE Reminders (
   reminder_id INT PRIMARY KEY,
   owner_id INT REFERENCES PolicyOwners(owner_id),
   reminder_date DATE NOT NULL,
   reminder_message TEXT
);

CREATE TABLE policy_owners (
    owner_id UUID PRIMARY KEY,
    owner_name VARCHAR(255),
    policy_expiration_date DATE
);

CREATE TABLE policies (
    policy_id UUID PRIMARY KEY,
    policy_name VARCHAR(255),
    owner_id UUID REFERENCES policy_owners(owner_id),
    expiration_date DATE
);

CREATE TABLE reminders (
    reminder_id UUID PRIMARY KEY,
    policy_id UUID REFERENCES policies(policy_id),
    reminder_date DATE
);

CREATE TABLE notification_logs (
    log_id UUID PRIMARY KEY,
    reminder_id UUID REFERENCES reminders(reminder_id),
    log_message TEXT,
    log_date TIMESTAMP
);

CREATE TABLE RenewalHistory (
    renewal_id UUID PRIMARY KEY,
    policy_id UUID REFERENCES Policies(policy_id),
    renewal_date DATE,
    renewal_amount DECIMAL
);

CREATE TABLE SubscriptionStatus (
    status_id UUID PRIMARY KEY,
    owner_id UUID REFERENCES PolicyOwners(owner_id),
    subscription_status VARCHAR(20),
    last_notification_date DATE
);
