);

CREATE TABLE strategy_analysis (
    analysis_id INT PRIMARY KEY,
    strategy_name VARCHAR(50),
    analysis_date DATE
);

CREATE TABLE property_insurance_claims (
    claim_id INT PRIMARY KEY,
    location VARCHAR(255),
    claim_amount DECIMAL,
    claim_date DATE
);

