tables.table_schema:
CREATE TABLE Policy_Holders (
   policy_id INT PRIMARY KEY,
   policy_holder_name VARCHAR(100),
   address TEXT,
   phone_number VARCHAR(15)
);

CREATE TABLE Flood_Zones (
   zone_id INT PRIMARY KEY,
   zone_name VARCHAR(50),
   risk_level VARCHAR(20)
);

CREATE TABLE Underwriting_Decisions (
   decision_id INT PRIMARY KEY,
   policy_id INT,
   decision_date DATE,
   decision_result VARCHAR(20)
);

CREATE TABLE FloodRiskFactors (
factor_id INT PRIMARY KEY,
factor_name VARCHAR(50),
factor_description TEXT
);

CREATE TABLE ClaimFactors (
claim_id INT,
factor_id INT,
FOREIGN KEY (claim_id) REFERENCES HistoricalClaims(claim_id),
FOREIGN KEY (factor_id) REFERENCES FloodRiskFactors(factor_id)
);
