tables.table_schema:
CREATE TABLE properties (
    id INT PRIMARY KEY,
    address VARCHAR,
    latitude FLOAT,
    longitude FLOAT
);

CREATE TABLE flood_prone_areas (
    area_id INT PRIMARY KEY,
    area_name VARCHAR,
    area_polygon GEOMETRY
);

CREATE TABLE flood_prone_areas (
	id INT PRIMARY KEY,
	area_name VARCHAR,
	coordinates GEOGRAPHY
);

CREATE TABLE property_details (
	property_id INT PRIMARY KEY,
	property_name VARCHAR,
	property_location GEOGRAPHY,
	risk_factor FLOAT
);

CREATE TABLE property_info (
   property_id INT PRIMARY KEY,
   property_name VARCHAR(255),
   property_address VARCHAR(255),
   property_location GEOGRAPHY
);

CREATE TABLE flood_data (
   flood_id INT PRIMARY KEY,
   flood_date DATE,
   flood_location GEOMETRY,
   flood_depth FLOAT
);

CREATE TABLE property_damage (
   damage_id INT PRIMARY KEY,
   property_id INT,
   damage_cost DECIMAL,
   repair_duration INT
);

CREATE TABLE FloodProneAreas (
    id INT PRIMARY KEY,
    area_name VARCHAR(255),
    location GEOGRAPHY,
    risk_level INT
);

CREATE TABLE Properties (
    id INT PRIMARY KEY,
    property_name VARCHAR(255),
    property_location GEOGRAPHY,
    property_value DECIMAL
);

CREATE TABLE RiskAssessment (
    id INT PRIMARY KEY,
    property_id INT,
    risk_score INT,
    assessment_date DATE
);

CREATE TABLE UserProfiles (
    id INT PRIMARY KEY,
    username VARCHAR(255),
    email VARCHAR(255),
    last_login TIMESTAMP
);
