tables.table_schema:
CREATE TABLE flood_occurrences (
    id INT PRIMARY KEY,
    location VARCHAR(255),
    date DATE,
    severity INT
);

CREATE TABLE reinsurance_strategies (
    id INT PRIMARY KEY,
    strategy_name VARCHAR(255),
    risk_coverage DECIMAL,
    premium DECIMAL
);

CREATE TABLE FloodOccurrence (
    id INT PRIMARY KEY,
    location VARCHAR(255),
    date DATE,
    severity INT
);

CREATE TABLE LossEstimation (
    id INT PRIMARY KEY,
    location VARCHAR(255),
    estimated_loss DECIMAL,
    date DATE
);

CREATE TABLE ReinsuranceStrategy (
    id INT PRIMARY KEY,
    strategy_name VARCHAR(255),
    coverage_amount DECIMAL,
    premium DECIMAL
);

CREATE TABLE PredictiveModel (
    id INT PRIMARY KEY,
    model_name VARCHAR(255),
    algorithm VARCHAR(255),
    trained_on DATE
);
