tables.table_schema:
CREATE TABLE CustomerProfiles (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50) NOT NULL,
    customer_age INT,
    risk_profile VARCHAR(20) DEFAULT 'low'
);

CREATE TABLE FloodInsurancePlans (
    plan_id INT PRIMARY KEY,
    plan_name VARCHAR(50) NOT NULL,
    coverage_amount DECIMAL(10, 2) NOT NULL,
    deductible_amount DECIMAL(10, 2) NOT NULL
);

CREATE TABLE CustomerCoverageSelection (
    selection_id INT PRIMARY KEY,
    customer_id INT REFERENCES CustomerProfiles(customer_id),
    plan_id INT REFERENCES FloodInsurancePlans(plan_id),
    selected_date DATE NOT NULL
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(255),
    email VARCHAR(255),
    phone_number VARCHAR(15)
);

CREATE TABLE coverage_options (
    option_id INT PRIMARY KEY,
    option_name VARCHAR(255),
    coverage_details TEXT,
    premium_amount DECIMAL(10, 2)
);
