tables.table_schema:
CREATE TABLE alerts (
    id INT PRIMARY KEY,
    city_name VARCHAR(255),
    alert_message TEXT,
    alert_timestamp TIMESTAMP
);

CREATE TABLE Alerts (
  alert_id UUID PRIMARY KEY,
  timestamp TIMESTAMP,
  message VARCHAR,
  severity INT
);

CREATE TABLE Warnings (
  warning_id UUID PRIMARY KEY,
  timestamp TIMESTAMP,
  message VARCHAR,
  risk_level INT
);

CREATE TABLE AlertLog (
  log_id UUID PRIMARY KEY,
  alert_id UUID REFERENCES Alerts(alert_id),
  timestamp TIMESTAMP,
  acknowledged BOOLEAN
);
