tables.table_schema:
CREATE TABLE adjusters (
 adjuster_id INT PRIMARY KEY,
 adjuster_name VARCHAR(50),
 adjuster_email VARCHAR(100)
);

CREATE TABLE policies (
 policy_id INT PRIMARY KEY,
 policy_number VARCHAR(20),
 policy_holder_name VARCHAR(50),
 policy_type VARCHAR(50)
);

CREATE TABLE policy_information (
	policy_id SERIAL PRIMARY KEY,
	insured_name VARCHAR(255),
	policy_type VARCHAR(50),
	premium_amount DECIMAL(10, 2)
);

CREATE TABLE documentation (
	document_id SERIAL PRIMARY KEY,
	policy_id INT,
	document_type VARCHAR(50),
	document_content TEXT
);

CREATE TABLE communication_logs (
	log_id SERIAL PRIMARY KEY,
	policy_id INT,
	log_date DATE,
	log_content TEXT
);

CREATE TABLE adjusters (
	adjuster_id SERIAL PRIMARY KEY,
	adjuster_name VARCHAR(255),
	adjuster_email VARCHAR(255)
);

CREATE TABLE adjusters (
    adjuster_id INT PRIMARY KEY,
    adjuster_name VARCHAR(255),
    adjuster_email VARCHAR(255),
    adjuster_phone VARCHAR(15)
);
