tables.table_schema:
CREATE TABLE policyholder_contacts (
    contact_id INT PRIMARY KEY,
    policyholder_id INT REFERENCES policyholders(id),
    email TEXT,
    phone_number TEXT
);

CREATE TABLE policyholder_claims (
    claim_id INT PRIMARY KEY,
    policyholder_id INT REFERENCES policyholders(id),
    claim_amount DECIMAL,
    claim_date DATE
);
