tables.table_schema:
CREATE TABLE claims_data (
	claim_id SERIAL PRIMARY KEY,
	policy_holder_name VARCHAR(50) NOT NULL,
	claim_amount DECIMAL NOT NULL,
	claim_date DATE NOT NULL
);

CREATE TABLE ClaimsData (
 claim_id INT PRIMARY KEY,
 policy_number VARCHAR(50),
 incident_date DATE,
 claim_amount DECIMAL
);

CREATE TABLE Claimants (
 claimant_id INT PRIMARY KEY,
 claim_id INT REFERENCES ClaimsData(claim_id),
 claimant_name VARCHAR(100),
 claimant_address VARCHAR(255)
);

CREATE TABLE claims_data (
  claim_id INT PRIMARY KEY,
  incident_date DATE,
  policy_number VARCHAR(50),
  claim_amount DECIMAL
);

CREATE TABLE policy_holders (
  policy_holder_id INT PRIMARY KEY,
  first_name VARCHAR(50),
  last_name VARCHAR(50),
  address VARCHAR(100)
);

CREATE TABLE insurance_companies (
  company_id INT PRIMARY KEY,
  company_name VARCHAR(100),
  company_address VARCHAR(100),
  company_email VARCHAR(50)
);

CREATE TABLE Insurers(
 insurer_id INT PRIMARY KEY,
 insurer_name VARCHAR(100),
 insurer_address VARCHAR(255),
 insurer_contact VARCHAR(50)
);

CREATE TABLE Claim_Documents(
 document_id INT PRIMARY KEY,
 claim_id INT,
 document_type VARCHAR(50),
 document_path VARCHAR(255)
);

CREATE TABLE LiabilityClaims (
	claim_id INT PRIMARY KEY,
	claim_date DATE,
	policy_id INT,
	claim_amount DECIMAL(10, 2)
);

CREATE TABLE Policyholders (
	policy_id INT PRIMARY KEY,
	policyholder_name VARCHAR(50),
	policy_start_date DATE,
	policy_type VARCHAR(20)
);

CREATE TABLE Insurers (
	insurer_id INT PRIMARY KEY,
	insurer_name VARCHAR(50),
	insurer_address VARCHAR(100)
);

CREATE TABLE ClaimHandlers (
	handler_id INT PRIMARY KEY,
	handler_name VARCHAR(50),
	handler_department VARCHAR(50)
);

CREATE TABLE IncidentDetails (
	incident_id INT PRIMARY KEY,
	claim_id INT,
	incident_date DATE,
	incident_description TEXT
);
