tables.table_schema:
CREATE TABLE insurance_policies (
    policy_id INT PRIMARY KEY,
    policy_holder VARCHAR(100) NOT NULL,
    coverage_limits DECIMAL NOT NULL,
    terms_conditions TEXT
);

CREATE TABLE PolicyDetails (
    policy_id INT PRIMARY KEY,
    policy_holder VARCHAR(255),
    coverage_limit DECIMAL(10, 2),
    terms_and_conditions TEXT
);

CREATE TABLE PolicyHolders (
    holder_id INT PRIMARY KEY,
    holder_name VARCHAR(255),
    holder_address VARCHAR(255),
    phone_number VARCHAR(20)
);

CREATE TABLE Claims (
    claim_id INT PRIMARY KEY,
    policy_id INT,
    claim_amount DECIMAL(10, 2),
    claim_date DATE
);

CREATE TABLE CoverageTypes (
    coverage_id INT PRIMARY KEY,
    coverage_name VARCHAR(255),
    premium_rate DECIMAL(5, 3)
);

CREATE TABLE PolicyCoverage (
    policy_id INT,
    coverage_id INT,
    PRIMARY KEY (policy_id, coverage_id)
);
