tables.table_schema:
CREATE TABLE PolicyEndorsements (
    endorsement_id INT PRIMARY KEY,
    policy_id INT,
    endorsement_details TEXT
);

CREATE TABLE PolicyEndorsements ( 
    endorsement_id INT, 
    policy_number VARCHAR(50), 
    endorsement_date DATE, 
    endorsement_details TEXT 
);

CREATE TABLE Endorsements (
	endorsement_id INT PRIMARY KEY,
	policy_id INT,
	endorsement_date DATE,
	endorsement_text TEXT
);

CREATE TABLE Policy_Holders (
	holder_id INT PRIMARY KEY,
	holder_name VARCHAR(50),
	holder_address VARCHAR(100),
	holder_contact_number VARCHAR(15)
);

CREATE TABLE Agents (
	agent_id INT PRIMARY KEY,
	agent_name VARCHAR(50),
	agent_contact_number VARCHAR(15),
	commission_rate DECIMAL(5,2)
);

CREATE TABLE Agents (
    agent_id SERIAL PRIMARY KEY,
    agent_name VARCHAR(255),
    agent_email VARCHAR(100),
    agent_phone VARCHAR(20)
);

CREATE TABLE Insured_Clients (
    client_id SERIAL PRIMARY KEY,
    client_name VARCHAR(255),
    client_address TEXT,
    client_phone VARCHAR(20)
);

CREATE TABLE Agency (
    agency_id SERIAL PRIMARY KEY,
    agency_name VARCHAR(255),
    agency_address TEXT,
    agency_phone VARCHAR(20)
);
