tables.table_schema:
CREATE TABLE LiabilityInsuranceProducts (
    product_id SERIAL PRIMARY KEY,
    product_name VARCHAR(255) NOT NULL,
    loss_ratio DECIMAL(10, 2) NOT NULL,
    profitability_metrics DECIMAL(10, 2) NOT NULL
);

CREATE TABLE LiabilityInsuranceProducts (
  product_id INT PRIMARY KEY,
  product_name VARCHAR(100),
  loss_ratio DECIMAL(5,2),
  profitability_metric DECIMAL(5,2)
);

CREATE TABLE LiabilityProducts (
  product_id INT PRIMARY KEY,
  product_name VARCHAR(255) NOT NULL,
  loss_ratio DECIMAL(5, 2) NOT NULL
);

CREATE TABLE ProfitabilityMetrics (
  metric_id INT PRIMARY KEY,
  portfolio_id INT NOT NULL,
  profit_margin DECIMAL(5, 2) NOT NULL,
  return_on_investment DECIMAL(5, 2) NOT NULL
);
