tables.table_schema:
CREATE TABLE claims_adjusters (
  id INT PRIMARY KEY,
  name VARCHAR(255),
  location VARCHAR(255),
  specialization VARCHAR(255)
);

CREATE TABLE investigations (
   investigation_id INT PRIMARY KEY,
   claim_id INT,
   investigator_name VARCHAR(50),
   investigation_date DATE
);

CREATE TABLE findings (
   finding_id INT PRIMARY KEY,
   investigation_id INT,
   finding_details TEXT,
   finding_date DATE
);

CREATE TABLE settlements (
   settlement_id INT PRIMARY KEY,
   claim_id INT,
   settlement_amount DECIMAL,
   settlement_date DATE
);

CREATE TABLE claims (
   claim_id INT PRIMARY KEY,
   policyholder_name VARCHAR(50),
   claim_date DATE,
   claim_status VARCHAR(20)
);

CREATE TABLE adjusters (
   adjuster_id INT PRIMARY KEY,
   adjuster_name VARCHAR(50),
   adjuster_email VARCHAR(50),
   adjuster_phone VARCHAR(15)
);
