tables.table_schema:
CREATE TABLE historical_data (
    date DATE NOT NULL,
    residence_id INT NOT NULL,
    water_consumed FLOAT NOT NULL
);

CREATE TABLE trends_analysis (
    trend_id INT NOT NULL,
    trend_description VARCHAR(255) NOT NULL
);

CREATE TABLE WaterConsumptionData (
    id INT PRIMARY KEY,
    date DATE,
    consumption_gallons DECIMAL
);

CREATE TABLE CustomerInfo (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50),
    address VARCHAR(255),
    email VARCHAR(50)
);

CREATE TABLE ConsumptionTrends (
    trend_id INT PRIMARY KEY,
    trend_name VARCHAR(50),
    trend_description TEXT
);

CREATE TABLE WaterConsumptionData (
  Date DATE,
  Location VARCHAR,
  Consumption INT
);

CREATE TABLE CustomerInfo (
  CustomerID INT,
  Name VARCHAR,
  Address VARCHAR,
  Email VARCHAR
);

CREATE TABLE MonthlyTrends (
  Month DATE,
  Location VARCHAR,
  ConsumptionTrend VARCHAR
);

CREATE TABLE WaterSources (
  SourceID INT,
  Location VARCHAR,
  SourceType VARCHAR
);

CREATE TABLE water_meter_data (
  meter_id INT PRIMARY KEY,
  location_id INT,
  consumption DECIMAL,
  reading_date DATE
);

CREATE TABLE location (
  location_id INT PRIMARY KEY,
  city VARCHAR(50),
  state VARCHAR(2),
  country VARCHAR(50)
);

CREATE TABLE meter_info (
  meter_id INT PRIMARY KEY,
  manufacturer VARCHAR(50),
  model VARCHAR(50),
  installation_date DATE
);

CREATE TABLE meter_reading_history (
  reading_id INT PRIMARY KEY,
  meter_id INT,
  reading DECIMAL,
  reading_date DATE
);

CREATE TABLE trend_analysis (
  analysis_id INT PRIMARY KEY,
  trend_description TEXT,
  trend_start_date DATE,
  trend_end_date DATE
);
