tables.table_schema:
CREATE TABLE water_usage_reports (
  report_id INT PRIMARY KEY,
  department_id INT,
  report_date DATE,
  water_consumed DECIMAL
);

CREATE TABLE departments (
  department_id INT PRIMARY KEY,
  department_name VARCHAR(255),
  department_head VARCHAR(255)
);

CREATE TABLE departments (
    department_id INT PRIMARY KEY,
    department_name VARCHAR(50) NOT NULL
);

CREATE TABLE water_usage (
    usage_id INT PRIMARY KEY,
    department_id INT REFERENCES departments(department_id),
    month VARCHAR(10) NOT NULL,
    water_consumed DECIMAL(10,2) NOT NULL
);

CREATE TABLE monthly_reports (
    report_id INT PRIMARY KEY,
    department_id INT REFERENCES departments(department_id),
    month VARCHAR(10) NOT NULL,
    total_water_consumed DECIMAL(10,2) NOT NULL
);

CREATE TABLE department (
  department_id INT PRIMARY KEY,
  department_name VARCHAR(255) NOT NULL
);

CREATE TABLE employee (
  employee_id INT PRIMARY KEY,
  employee_name VARCHAR(255) NOT NULL,
  department_id INT,
  FOREIGN KEY (department_id) REFERENCES department(department_id)
);

CREATE TABLE department (
  department_id UUID PRIMARY KEY,
  department_name STRING
);

CREATE TABLE employee (
  employee_id UUID PRIMARY KEY,
  employee_name STRING,
  department_id UUID,
  FOREIGN KEY (department_id) REFERENCES department(department_id)
);

CREATE TABLE water_meter (
  meter_id UUID PRIMARY KEY,
  meter_location STRING,
  department_id UUID,
  FOREIGN KEY (department_id) REFERENCES department(department_id)
);
